package a_3_17;

import java.io.CharArrayReader;
import java.util.HashMap;

/**
 * @author caohongye
 *如你可以用 chars 中的『字母』（字符）拼写出 words 中的某个『单词』（字符串），那么我们就认为你掌握了这个单词。
 *注意：每次拼写时，chars 中的每个字母都只能用一次。
 *返回词汇表 words 中你掌握的所有单词的 长度之和。
 *哈希表的应用！！ / int[26]的引入
 */
public class CountCharacters {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] ch=new int[26];
		ch['a']=1;
		System.out.println(ch[0]);
	}
	public int countCharacters(String[] words, String chars) {
	    int[] chars_count = count(chars); // 统计字母表的字母出现次数
	    int res = 0;
	    for (String word : words) {
	        int[] word_count = count(word); // 统计单词的字母出现次数
	        if (contains(chars_count, word_count)) {
	            res += word.length();
	        }
	    }
	    return res;
	}

	// 检查字母表的字母出现次数是否覆盖单词的字母出现次数
	boolean contains(int[] chars_count, int[] word_count) {
	    for (int i = 0; i < 26; i++) {
	        if (chars_count[i] < word_count[i]) {
	            return false;
	        }
	    }
	    return true;
	}

	// 统计 26 个字母出现的次数
	int[] count(String word) {
	    int[] counter = new int[26];
	    for (int i = 0; i < word.length(); i++) {
	        char c = word.charAt(i);
	        counter[c-'a']++;
	    }
	    return counter;
	}
}
